import{_ as d,C as r,c as o,o as e,a3 as a,b as n,w as l,a as s,G as p,a4 as h}from"./chunks/framework.CUcrqFol.js";const D=JSON.parse('{"title":"D.1 对话式 AI 对比","description":"D.1 对话式 AI 对比 对话式 AI 是最简单的 AI 编程方式：直接对话，让 AI 帮你写代码。以下是主流工具的对比。 综合对比表 | 工具 | 公司 | 中文支持 | 免费额度 | 代码能力 | 适合人群 | |------|------|----------|----------|----------|---","frontmatter":{"title":"D.1 对话式 AI 对比"},"headers":[],"relativePath":"Basic/99-appendix/d-1-content.md","filePath":"Basic/99-appendix/d-1-content.md","lastUpdated":1765094268000}'),g={name:"Basic/99-appendix/d-1-content.md"};function u(c,t,E,B,m,A){const i=r("Mermaid");return e(),o("div",null,[t[1]||(t[1]=a('<h1 id="d-1-对话式-ai-对比" tabindex="-1">D.1 对话式 AI 对比 <a class="header-anchor" href="#d-1-对话式-ai-对比" aria-label="Permalink to &quot;D.1 对话式 AI 对比&quot;">​</a></h1><p>对话式 AI 是最简单的 AI 编程方式：直接对话，让 AI 帮你写代码。以下是主流工具的对比。</p><h2 id="综合对比表" tabindex="-1">综合对比表 <a class="header-anchor" href="#综合对比表" aria-label="Permalink to &quot;综合对比表&quot;">​</a></h2><table tabindex="0"><thead><tr><th>工具</th><th>公司</th><th>中文支持</th><th>免费额度</th><th>代码能力</th><th>适合人群</th></tr></thead><tbody><tr><td><strong>ChatGPT</strong></td><td>OpenAI</td><td>良好</td><td>有限制</td><td>优秀</td><td>通用场景</td></tr><tr><td><strong>Claude</strong></td><td>Anthropic</td><td>优秀</td><td>有限制</td><td>优秀</td><td>长文本/复杂任务</td></tr><tr><td><strong>Gemini</strong></td><td>Google</td><td>良好</td><td>较多</td><td>良好</td><td>Google 生态用户</td></tr><tr><td><strong>Kimi</strong></td><td>月之暗面</td><td>原生</td><td>较多</td><td>良好</td><td>国内用户</td></tr><tr><td><strong>DeepSeek</strong></td><td>DeepSeek</td><td>原生</td><td>免费</td><td>优秀</td><td>性价比首选</td></tr><tr><td><strong>豆包</strong></td><td>字节跳动</td><td>原生</td><td>免费</td><td>良好</td><td>国内新手</td></tr></tbody></table><h2 id="各工具详解" tabindex="-1">各工具详解 <a class="header-anchor" href="#各工具详解" aria-label="Permalink to &quot;各工具详解&quot;">​</a></h2><h3 id="chatgpt" tabindex="-1">ChatGPT <a class="header-anchor" href="#chatgpt" aria-label="Permalink to &quot;ChatGPT&quot;">​</a></h3><p><strong>官网</strong>：chat.openai.com</p><p><strong>特点</strong>：</p><ul><li>AI 对话的「开山鼻祖」，生态最丰富</li><li>GPT-4 代码能力非常强</li><li>插件和 GPTs 扩展功能多</li></ul><p><strong>限制</strong>：</p><ul><li>需要科学上网</li><li>免费版功能受限</li></ul><p><strong>适合</strong>：想体验最成熟 AI 能力的用户</p><h3 id="claude" tabindex="-1">Claude <a class="header-anchor" href="#claude" aria-label="Permalink to &quot;Claude&quot;">​</a></h3><p><strong>官网</strong>：claude.ai</p><p><strong>特点</strong>：</p><ul><li>超长上下文窗口（可处理很长的代码）</li><li>推理能力强，适合复杂任务</li><li>中文理解能力优秀</li><li>更不容易产生幻觉</li></ul><p><strong>限制</strong>：</p><ul><li>需要科学上网</li><li>免费版有对话次数限制</li></ul><p><strong>适合</strong>：处理长代码、复杂逻辑的用户</p><h3 id="gemini" tabindex="-1">Gemini <a class="header-anchor" href="#gemini" aria-label="Permalink to &quot;Gemini&quot;">​</a></h3><p><strong>官网</strong>：gemini.google.com</p><p><strong>特点</strong>：</p><ul><li>Google 出品，与 Google 服务整合好</li><li>多模态能力强（可以理解图片）</li><li>免费额度较多</li></ul><p><strong>限制</strong>：</p><ul><li>需要科学上网</li><li>代码能力略逊于 GPT-4 和 Claude</li></ul><p><strong>适合</strong>：使用 Google 生态的用户</p><h3 id="kimi" tabindex="-1">Kimi <a class="header-anchor" href="#kimi" aria-label="Permalink to &quot;Kimi&quot;">​</a></h3><p><strong>官网</strong>：kimi.moonshot.cn</p><p><strong>特点</strong>：</p><ul><li>国内可直接访问</li><li>超长上下文支持</li><li>中文理解优秀</li><li>可以读取文件和网页</li></ul><p><strong>限制</strong>：</p><ul><li>代码能力相对弱一些</li><li>有使用次数限制</li></ul><p><strong>适合</strong>：国内用户的入门首选</p><h3 id="deepseek" tabindex="-1">DeepSeek <a class="header-anchor" href="#deepseek" aria-label="Permalink to &quot;DeepSeek&quot;">​</a></h3><p><strong>官网</strong>：chat.deepseek.com</p><p><strong>特点</strong>：</p><ul><li>国内可直接访问</li><li>代码能力非常强（DeepSeek Coder）</li><li>完全免费使用</li><li>性价比极高</li></ul><p><strong>限制</strong>：</p><ul><li>知名度相对较低</li><li>生态不如 ChatGPT 丰富</li></ul><p><strong>适合</strong>：想要高质量免费工具的用户，强烈推荐</p><h3 id="豆包" tabindex="-1">豆包 <a class="header-anchor" href="#豆包" aria-label="Permalink to &quot;豆包&quot;">​</a></h3><p><strong>官网</strong>：www.doubao.com</p><p><strong>特点</strong>：</p><ul><li>字节跳动出品</li><li>国内可直接访问</li><li>界面友好，上手简单</li><li>免费使用</li></ul><p><strong>限制</strong>：</p><ul><li>代码能力一般</li><li>更适合日常对话</li></ul><p><strong>适合</strong>：国内完全零基础的新手</p><h2 id="选择建议" tabindex="-1">选择建议 <a class="header-anchor" href="#选择建议" aria-label="Permalink to &quot;选择建议&quot;">​</a></h2>',48)),(e(),n(h,null,{default:l(()=>[p(i,{id:"mermaid-445",class:"mermaid",graph:"flowchart%20TD%0A%20%20%20%20A%5B%E4%BD%A0%E5%9C%A8%E5%93%AA%E9%87%8C%3F%5D%20--%3E%20B%7B%E8%83%BD%E7%A7%91%E5%AD%A6%E4%B8%8A%E7%BD%91%E5%90%97%3F%7D%0A%20%20%20%20B%20--%3E%7C%E8%83%BD%7C%20C%7B%E8%A6%81%E5%85%8D%E8%B4%B9%E8%BF%98%E6%98%AF%E4%BB%98%E8%B4%B9%3F%7D%0A%20%20%20%20B%20--%3E%7C%E4%B8%8D%E8%83%BD%7C%20D%5B%E5%9B%BD%E5%86%85%E5%B7%A5%E5%85%B7%5D%0A%20%20%20%20C%20--%3E%7C%E5%85%8D%E8%B4%B9%7C%20E%5BClaude%20%2F%20Gemini%20%E5%85%8D%E8%B4%B9%E7%89%88%5D%0A%20%20%20%20C%20--%3E%7C%E4%BB%98%E8%B4%B9%7C%20F%5BChatGPT%20Plus%20%2F%20Claude%20Pro%5D%0A%20%20%20%20D%20--%3E%20G%7B%E4%B8%BB%E8%A6%81%E7%94%A8%E9%80%94%3F%7D%0A%20%20%20%20G%20--%3E%7C%E5%86%99%E4%BB%A3%E7%A0%81%7C%20H%5BDeepSeek%5D%0A%20%20%20%20G%20--%3E%7C%E6%97%A5%E5%B8%B8%E5%AF%B9%E8%AF%9D%7C%20I%5BKimi%20%2F%20%E8%B1%86%E5%8C%85%5D%0A"})]),fallback:l(()=>[...t[0]||(t[0]=[s(" Loading... ",-1)])]),_:1})),t[2]||(t[2]=a('<h2 id="快速推荐" tabindex="-1">快速推荐 <a class="header-anchor" href="#快速推荐" aria-label="Permalink to &quot;快速推荐&quot;">​</a></h2><table tabindex="0"><thead><tr><th>你的情况</th><th>推荐工具</th></tr></thead><tbody><tr><td>国内用户，想写代码</td><td>DeepSeek</td></tr><tr><td>国内用户，入门体验</td><td>Kimi</td></tr><tr><td>能科学上网，追求最强</td><td>Claude / ChatGPT</td></tr><tr><td>能科学上网，想免费用</td><td>Gemini</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">建议</p><p>不用纠结选哪个，<strong>先用起来</strong>最重要。等你熟悉了，再根据需要切换工具。</p></div>',3))])}const b=d(g,[["render",u]]);export{D as __pageData,b as default};
